mapper.select:
SELECT FREEBOARDSEQ, USERID, FREEBOARDTITLE, FREEBOARDCONTENT, REGDATE, READCOUNT
FROM FREEBOARD
WHERE FREEBOARDSEQ = #{freeboardseq}

mapper.update:
UPDATE FREEBOARD
SET FREEBOARDTITLE = #{freeboardtitle}, FREEBOARDCONTENT = #{freeboardcontent}
WHERE FREEBOARDSEQ = #{freeboardseq}

UPDATE FREEBOARD
SET READCOUNT = READCOUNT + 1 
WHERE FREEBOARDSEQ = #{freeboardseq}
