mapper.select:
SELECT PROJECTITEMSEQ, PROJECTITEMNAME, QUANTITY, PROJECTID, PRICE
FROM PROJECTITEM
WHERE PROJECTITEMSEQ = #{projectItemSeq}

mapper.update:
UPDATE PROJECTS SET TOTALPRICE = #{totalPrice} + #{price} 
WHERE PROJECTID = (SELECT PROJECTID FROM PROJECTITEM 
WHERE PROJECTITEMSEQ = #{projectItemSeq})
