mapper.select:
  		SELECT NOTICESEQ, USERID, NOTICETITLE, NOTICECONTENT, REGDATE
		FROM NOTICE
		WHERE NOTICESEQ = #{noticeseq}

mapper.update:
UPDATE NOTICE
SET NOTICETITLE = #{noticetitle}, NOTICECONTENT = #{noticecontent}
WHERE NOTICESEQ = #{noticeseq}

